import React from "react"
class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            id:1,
            name : "vijay",
            mobile: 8952266221,
            count:0,
            value:""
        }
    }
     change=()=>{
        this.setState({
            id:2,
            name:"roopa",
            mobile:856321479
        })
     }
inc=()=>{
    this.setState({
        count:this.state.count+1
    })
}
dec=()=>{
    this.setState({
        count:this.state.count-1
    })
}

getData=(e)=>{
    this.setState({
        value:e.target
    })
}
    render(){
        return(
            <div>
                STATES
           <h1>constructor method</h1>
           <p>
            ID: {this.state.id}<br></br>
            Name:{this.state.name}<br></br>
            Mobile:{this.state.mobile}
           </p>
           <button onClick={this.change}>Update</button>
           <br></br>
           <h4>Count: {this.state.count}</h4>
           <button onClick={this.inc}>Inc</button>
           <button onClick={this.dec}>dec</button>
           <br></br> <br></br>

            <input type="text"></input>
           <p>current user input value : {this.state.value}</p>
           <App1 value={this.state.id} name={this.state.name} mobile={this.state.mobile} count={this.state.count}> </App1>
            </div>
        )
    }
}

class App1 extends React.Component{
    render(){
        return(
            <div>
                
                <h2>Child Component</h2>
                <h2>{this.props.count}</h2>
                <p>
            ID: {this.props.value}<br></br>
            Name:{this.props.name}<br></br>
            Mobile:{this.props.mobile}
           </p>
            </div>
        )
    }
}
export default App;
