    let data="hello"
    let x = "Good"
    return(
        <div>
            Props 
            <h2>function component {data}</h2>
            <App1 a = {data} send={x}></App1>
           
        </div>
    )
}

function App1 (props){
    const {a,send}=props
    return(
        <div>
            <h3>Child Component {a} and {send}</h3>
            <App2 data={a} y={send}></App2>
        </div> 
    )
}

function App2 (props){
    const {data,y}=props
    return(
        <div>
            <h3> 2nd Child Component {props.data} {props.y}</h3>
        </div>
    )
}
