import React from "react";

class Login extends React.Component{
    constructor(){
        super()
        this.state={
            user:"",
            email:"",
            pswd:""
        }
    }
    updateUser =(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
            }
            updateEmail=(e)=>{
                this.setState({
                    email:e.target.value
                })
            }
            updatePassword=(e)=>{
                this.setState({
                    pswd:e.target.value
                })
            }

            submitForm=()=>{
               let x= localStorage.getItem("user")
               let y= localStorage.getItem("email")
              let z= localStorage.getItem("password")
                if(this.state.user != x || this.state.email != y || this.state.pswd != z){
                    alert("Check the credentials")
                }
                else{
                    alert("Logged In....")
                }
            }
    render(){
        return(
            <div>
                <h1>Login</h1>
                <form onSubmit={this.submitForm}>
                <label>UserName:</label>
                    <input value = {this.state.user}  onChange={this.updateUser} required type="text"></input>
                    <br></br>
                    <label>Email:</label>
                    <input value = {this.state.email} onChange={this.updateEmail} required type="email" ></input>
                    <br></br>
                    <label>Password:</label>
                    <input value = {this.state.pswd} onChange={this.updatePassword} required type="password" ></input>
                    <br></br>
                    <input type="submit" value="Login"></input>
                </form>
            </div>
        )
    }
}
export default Login;
