import React from "react";
class App extends React.Component{
    constructor(){
        super();
        this.state={
            user: " ",
            email:" ",
            pswd:" ",
            cpswd:""
        }
    }
    updateuser=(e)=>{
        console.log(e.target.value)
    }
    render(){
        return(
            <div>
                <form>
                    <label>UserName:</label>
                    <input value = {this.state.user} type="text" onChange={this.updateuser}></input>
                    <br></br>
                    <label>Email:</label>
                    <input value = {this.state.email} type="email"></input>
                    <br></br>
                    <label>Password:</label>
                    <input value = {this.state.pswd} type="password"></input>
                    <br></br>
                    <label>Confirm Password:</label>
                    <input value = {this.state.cpswd} type="password"></input>
                    <br></br>
                    <input type="submit" value="Register"></input>
                </form>
            </div>
        )
    }
}
export default App;
