import { useEffect, useState } from "react";
import "./index.css"

function App(){
    const [data,setData]=useState(null)
    useEffect(()=>{
        fetch("http://localhost:3007/Employee")  //http://localhost:3007/Employee
        .then((res)=>{
            return res.json()
        })
        .then((res)=>{
            console.log(res)
            setData(res)
        })
    })
    return(
        <div>
            <h1>Fetch data from backend</h1>
             <table>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>City</th>
                        <th>Mobile</th>
                    </tr>
                </thead>
                <tbody>
                {data.map((item)=>(
                    <tr>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.city}</td>
                        <td>{item.mobile}</td>
                    </tr>
                     ))}
                </tbody>
             </table>
        </div>
    )
}
export default App;




