import { useEffect, useState } from "react";
import "./index.css"
import { Link, useNavigate } from "react-router-dom";


function StudentList() {
    const [data, setData] = useState(null)
    const navigate = useNavigate()
    useEffect(() => {
        fetch("http://localhost:3008/Students")
            .then((res) => {
                return res.json()
            })
            .then((res) => {
                console.log(res)
                setData(res)
            })
    }, [])

    const deleteData = (id) => {
        if (window.confirm("Are you sure to delete the Entry")) {
            fetch("http://localhost:3008/Students/" + id, {
                method: "DELETE"
            })
                .then((s) => {
                    alert("Deleted  Successfully")
                    console.log(s)
                    window.location.reload()
                })
                .catch((err) => {
                    console.log(err)
                })
        }
    }

    const editData = (id) => {
        navigate("/studedit/" + id)
    }
    
    const detailsData = (id) => {
        navigate("/studdetails/" + id)
    }

    return (
        <div className="container">
            <div className="card">
                <div className="card-title">
                    <h1>Student Management System</h1>
                </div>
                <div className="card-body">
                    <Link to="/form" className="btn btn-success">Add New(+)</Link>
                    <form>
                    <input type="text" placeholder="Filter Records" disabled="disabled" className="form-control" id="exampleInputName" />
                      <button type="">Search</button>
                    </form>

                    <table className="table table-bordered">
                        <thead className="table-dark text-white">
                            <tr>
                                <th>Id</th>
                                <th>Name</th>
                                <th>Grade</th>
                                <th>Mobile</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data?.map((item) => (
                                <tr>
                                    <td>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.grade}</td>
                                    <td>{item.mobile}</td>
                                    <td>
                                        <button onClick={() => { deleteData(item.id) }} className="btn btn-danger">Delete</button>
                                        <button onClick={() => editData(item.id)} className="btn btn-primary" >Edit</button>
                                        <button onClick={() => detailsData(item.id)} className="btn btn-success">Show Details</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
export default StudentList;
