import React from "react"
class App extends React.Component{
   
        state = {
            id:1,
            name:"roopa",
            mobile:7856321409,
            count:0,
            value:" "
        }
    
 
    change = ()=>{
      this.setState({
        id:2,
        name:"deepu",
        mobile:9321569874
      })
    }
 
    inc=()=>{
        this.setState({
            count:this.state.count+1
        })
    }

    dec=()=>{
        this.setState({
            count:this.state.count-1
        })
    }

    reset=()=>{
        this.setState({
            count: 0
        })
    }

    getData=(e)=>{
        this.setState({
            value:e.target.value
        })
    }
    render()
    {
        return(
            <div>
            <h1>Without Constructor Method</h1>
            <p>
                ID: {this.state.id}<br/>
                Name:{this.state.name}<br/>
                mobile:{this.state.mobile}
            </p>
            <button onClick={this.change}>Update</button><br/>
            <h4>Count:{this.state.count}</h4>
            <button onClick={this.inc}>increment</button>
            <button onClick={this.dec}>decrement</button>
            <button onClick={this.reset}>Reset</button><br/><br/>
            <input type="text" onChange={this.getData}/>
            <p>Current user input values: {this.state.value}</p>
            <App1 count = {this.state.count} value={this.state.id} name={this.state.name} mobile={this.state.mobile}/>
           
            </div>
        )
    }
}

class App1 extends React.Component{
    render(){
        return(
            <div>
                <h2>Child Component</h2>
                <h2>{this.props.count}</h2>
                <p>
                ID: {this.props.value}<br/>
                Name:{this.props.name}<br/>
                mobile:{this.props.mobile}
            </p>
            </div>
        )
    }
}


export default App;
