import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";


function StudentForm(){
    const [id,setId]=useState("")
    const [name,setName]=useState("")
    const [claass,setClaass]=useState("")
    const [mobile,setMobile]=useState("")

    const navigate=useNavigate()


const sendData=(e)=>{
    e.preventDefault
    const data={id,name,claass,mobile}
    fetch("http://localhost:3008/Students",{
        method:"POST",
        headers:{"Content-type":"application/json"},
        body: JSON.stringify(data)
    })
    .then(()=>{
        alert("Data Saved")
        navigate ("/")
    })
    .catch((err)=>{
        alert("error"+err)
    })
   
}

    return(
        <div className="container">
            <h2>Add New Student Data</h2>
            <form onSubmit={sendData}>
            <div class="mb-3">
    <label  class="form-label">ID</label>
    <input type="text" value={id} onChange={e=>setId(e.target.value)} disabled="disabled" class="form-control" id="exampleInputName" />
     </div>
    <div class="mb-3">
    <label class="form-label">Student Name</label>
    <input type="text" value={name} onChange={e=>setName(e.target.value)} class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
     </div>
    <div class="mb-3">
    <label  class="form-label">Class</label>
    <input type="text" value={claass} onChange={e=>setClaass(e.target.value)} class="form-control" id="exampleInputPassword1"/>
    </div>
    <div class="mb-3">
    <label  class="form-label">Mobile</label>
    <input type="Number" value={mobile} onChange={e=>setMobile(e.target.value)} class="form-control" id="exampleInputPassword1"/>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
     <Link to="/" className="btn btn-danger">Back</Link>
    </form>
        </div>
    )
}
export default StudentForm;
