// States --> pass the information in the same component

//constructor method

import React from "react"
class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            id:1,
            name:"roopa",
            mobile:7856321409
        }
    }
    render()
    {
        return(
            <div>
            <h1>Constructor Method</h1>
            <p>
                ID: {this.state.id}
            </p>
            </div>
        )
    }
}
export default App;
