function App(){
    const [data,setData]=useState("React Js")
    const [count,setCount]=useState(0)

    const change=()=>{
        setData("Helo")
    }
    const inc=()=>{
        setCount(count+1)
    }
    return(
        <div>
        <h2>Hooks Methods {data}</h2>
     <Data value={data} count={count}/>
     <button onClick={change}>Update</button>
     <br/><br/>
     <p>Count:{count}</p>
     <button onClick={inc}>Inc</button>
        </div>
    )
}

function Data(props){
    const {value,count}=props
    return(
        <div>
            <h3>Child Component</h3>
            <p>We are learning {value}</p>
            <h2>{count}</h2>
        </div>
    )
}

export default App
