import Register from "./Hook.js";
import {BrowserRouter as Router,Routes,Route} from "react-router-dom"

function App(){
    return(
        <div>
          <Router>
            <Routes>
               <Route path="/" element={<Register/>}/>
               <Route path="login" element={<Login/>}/>
            </Routes>
          </Router>
        </div>
    )
}
export default App;
