// States --> pass the information in the same component

//constructor method

import React from "react"
class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            id:1,
            name:"roopa",
            mobile:7856321409
        }
    }
    render()
    {
        return(
            <div>
            <h1>Constructor Method</h1>
            {/* <p>
                ID: {this.state.id}<br/>
                Name:{this.state.name}<br/>
                mobile:{this.state.mobile}
            </p> */}
            <App1 value={this.state.id} name={this.state.name} mobile={this.state.mobile}/>
            </div>
        )
    }
}

class App1 extends React.Component{
    render(){
        return(
            <div>
                <h2>Child Component</h2>
                <p>
                ID: {this.props.value}<br/>
                Name:{this.state.name}<br/>
                mobile:{this.state.mobile}
            </p>
            </div>
        )
    }
}


export default App;
