import React from "react";
class App extends React.Component{
    constructor(){
        super();
        this.state={
            user: " ",
            email:" ",
            pswd:" ",
            cpswd:""
        }
    }
    updateuser=(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword = (e)=>{
        this.setState({
            password:e.target.value
        })
    }
    updateCpassword = (e)=>{
        this.setState({
            Cpassword:e.target.value
        })
    }
    submitData=()=>{
        if(this.state.userlength <=5){
            alert("UserName should contain more than fivr character")
        }
        else if(this.state.pswd != this.state.cpswd){
            alert("Password doesnot ")
        }
    }
    render(){
        return(
            <div>
                <form>
                    <label>UserName:</label>
                    <input value = {this.state.user} type="text" onChange={this.updateuser}></input>
                    <br></br>
                    <label>Email:</label>
                    <input value = {this.state.email} type="email" onChange={this.updateEmail}></input>
                    <br></br>
                    <label>Password:</label>
                    <input value = {this.state.pswd} type="password" onChange={this.updatePassword}></input>
                    <br></br>
                    <label>Confirm Password:</label>
                    <input value = {this.state.cpswd} type="password" onChange={this.updateCpassword}></input>
                    <br></br>
                    <input type="submit" value="Register"></input>
                </form>
            </div>
        )
    }
}
export default App;
