//form validation using usestate()

function Register(){
   const [user,setUser]=useState("")
   const [email,setEmail]=useState("")
   const [pswd,setPswd]=useState("")
   const [cpswd,setCpswd]=useState("")

   const changeUser=(e)=>{
    setUser(e.target.value)
   }

   const changeEmail=(e)=>{
    setEmail(e.target.value)
   }

   const changePswd=(e)=>{
    setPswd(e.target.value)
   }

   const changeCpswd=(e)=>{
    setCpswd(e.target.value)
   }

   const submitData=()=>{
    if(user.length<=5){
        alert("user shoud be enter >5 characters")
    }
    else if(pswd !== cpswd){
        alert("Pasword doesnot match")
    }
    else{
        alert("form successfully submitted")
    }
   }
    return(
        <div>
            <h1>Registration</h1>
            <form onSubmit={submitData}>
                    <label>UserName:</label>
                    <input value={user}  required type="text" onChange={changeUser}></input>
                    <br></br><br></br>
                    <label>Email:</label>
                    <input value={email} required type="email" onChange={changeEmail} ></input>
                    <br></br><br></br>
                    <label>Password:</label> 
                    <input value={pswd} required type="password" onChange={changePswd} ></input>
                    <br></br><br></br>
                    <label>Confirm Password:</label>
                    <input value={cpswd}  required type="password" onChange={changeCpswd}></input>
                    <br></br><br></br>
                    <input type="submit" value="Register"></input>
                </form>
        </div>
    )
}
export default App;
