import React  from "react";
class App extends React.Component{
    render(){
        return(
            <div>
                <h1>Class Component</h1>
            </div>
        )
    }
}

class App1 extends App{
    render(){
        const parent = super.render()
        return(
            <div>
                {parent}
                <h2>Parent element</h2>
            </div>   
        )
    }
}

class App2 extends App1{
    render(){
        const parent = super.render()
        return(
            <div>
                {parent}
                <h2>
                    Child element
                </h2>
            </div>
        )
    }
}

export default App2;
