import React from "react";
class App extends React.Component{
    constructor(){
        super();
        this.state={
            user:"",
            email:"",
            pswd:"",
            cpswd:""
        }
    }
    updateUser =(e)=>{
console.log(e.target.value)
this.setState({
    user:e.target.value
})
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
    updateCpassword=(e)=>{
        this.setState({
            cpswd:e.target.value
        })
    }

    submitData=()=>{
        if(this.state.user.length <=5){
            alert("enter valid user name")
        }
        else if(this.state.pswd !== this.state.cpswd){
            alert("password does not match")
        }
        else{
            alert("successfuly registered")
        }
    }
    render(){
        return(
            <div>
                <form onSubmit={this.submitData}>
                    <label>UserName:</label>
                    <input value = {this.state.user}  onChange={this.updateUser} required type="text"></input>
                    <br></br>
                    <label>Email:</label>
                    <input value = {this.state.email} onChange={this.updateEmail} required type="email" ></input>
                    <br></br>
                    <label>Password:</label>
                    <input value = {this.state.pswd} onChange={this.updatePassword} re type="password" ></input>
                    <br></br>
                    <label>Confirm Password:</label>
                    <input value = {this.state.cpswd} onChange={this.updateCpassword} type="password" ></input>
                    <br></br>
                    <input type="submit" value="Register"></input>
                </form>
            </div>
        )
    }
}
export default App;
