import { useParams } from "react-router-dom"
import "./Student.css"
import { useEffect, useState } from "react"


function StudentDetails() {
    const [data, setData] = useState(null)
    const {studid}=useParams()
    useEffect(() => {
        fetch("http://localhost:3008/Students/"+studid)
            .then((res) => {
                return res.json()
            })
            .then((res) => {
                console.log(res)
                setData(res)
            })
    }, [])

    return (
        <div className="container-fluid" >
            <div className="card" style={{width: "18rem"}}>
                {data && 
                <div className="card-body">
                    <h5 className="card-title">Student Id:{data.id}</h5>
                    <h6 className="card-subtitle mb-2 text-body-secondary">Student Name:{data.name}</h6>
                    <p className="card-text">Grade:{data.grade}</p>
                    <p className="card-text">Mobile:{data.mobile}</p>
                    <a href="#" className="card-link">Card link</a>
                    <a href="#" className="card-link">Another link</a>
                </div>
}
            </div>
        </div>
    )
}
export default StudentDetails;


